async ({ description, language, returnType }) => { const templates = { javascript: (desc, ret) => `function ${desc.toLowerCase().replace(/\s+/g, '_')}() {
  // ${desc}
  return ${ret || 'null'};
}`, typescript: (desc, ret) => `function ${desc.toLowerCase().replace(/\s+/g, '_')}(): ${ret || 'void'} {
  // ${desc}
  return ${ret ? 'null' : undefined};
}`, python: (desc, ret) => `def ${desc.toLowerCase().replace(/\s+/g, '_')}():
    """${desc}"""
    return ${ret || 'None'}`, java: (desc, ret) => `public ${ret || 'void'} ${desc.toLowerCase().replace(/\s+/g, '_')}() {
    // ${desc}
    return${ret ? ' null' : ''};
}` }; const code = templates[language](description, returnType); return { content: [{ type: 'text', text: code }] }; }

